public class MultiplicationTableNestedLoop {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	 for(int i = 0; i<=5; i++) {
		 System.out.println("$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$");
		 System.out.println("I am the Parent loop");
		 for (int j = 0; j <= 2; j++) {
			 System.out.println("*************************************************");
			 System.out.println("I am the child loop");
			
		}
	 }
		


	}}
